mapper.select:

where left(longitude,9) = left(#{lng},9) and left(latitude,8) =
left(#{lat},8)
 and id != #{treeId}

mapper.update:
update tree
name = #{name}, tree_code = #{treeCode}, size = #{size}, longitude = #{longitude}, latitude = #{latitude}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = NOW(), take_care_day = #{takeCareDay}, create_new = #{createNew}, address = #{address}, provinces = #{provinces}, cities = #{cities}, wards = #{wards}, water_the_tree = #{waterTheTree}, prune = #{prune}, fertilize = #{fertilize}, against_tree = #{againstTree}, clean_the_stump = #{cleanTheStump}, status_clean_the_stump = #{statusCleanTheStump}, status_against_tree = #{statusAgainstTree}, status_fertilize = #{statusFertilize}, status_prune = #{statusPrune}, status_water_the_tree = #{statusWaterTheTree}, type = #{type}, status = #{status}, status_update = #{statusUpdate}, image = #{image},
where id = #{id}

mapper.select:
SELECT
(SELECT COUNT(*) FROM tree
WHERE status=0

	and id in

		#{id}


) as good,
(SELECT COUNT(*) FROM tree
WHERE status=1

	and id in

		#{id}


) as kha,
(SELECT COUNT(*) FROM tree
WHERE status=2

	and id in

		#{id}


) as medium,
(SELECT COUNT(*) FROM tree
WHERE status=3

	and id in

		#{id}


) as needCare,
(SELECT COUNT(*) FROM tree
WHERE status>=4

	and id in

		#{id}


) as immediately

SELECT g1.name as wardName,
(SELECT COUNT(*) FROM tree f2
WHERE f2.wards
= f1.wards AND type = 0

	and f2.id in

		#{id}


) as type0,
(SELECT COUNT(*) FROM tree f2
WHERE f2.wards = f1.wards AND
type = 1

	and f2.id in

		#{id}


) as type1,
(SELECT COUNT(*) FROM tree f2
WHERE f2.wards = f1.wards AND
type = 2

	and f2.id in

		#{id}


) as type2,
(SELECT COUNT(*) FROM tree f2
WHERE f2.wards = f1.wards AND
type = 3

	and f2.id in

		#{id}


) as type3
FROM tree f1 INNER JOIN (SELECT id,name FROM wards) g1 on
f1.wards = g1.id

	where f1.id in

		#{id}


GROUP BY wards
